execution_plan.query:
SELECT users.id AS s1__id, users.username AS s1__username, users.full_name AS s1__full_name
FROM users
WHERE EXISTS (
  SELECT 1 FROM chat_members WHERE chat_members.user_id = users.id
)
AND NOT EXISTS (
  SELECT 1 FROM messages WHERE messages.sended_by = users.id
    AND messages.chat_id IN (
      SELECT chat_id FROM chat_members WHERE chat_members.user_id = users.id
    )
);
