SELECT Orders.order_status_code AS s1__order_status_code, COUNT(Orders.order_id) AS s1__order_count
FROM Orders
GROUP BY Orders.order_status_code
HAVING COUNT(Orders.order_id) = (
  SELECT MIN(c) FROM (
    SELECT COUNT(order_id) AS c FROM Orders GROUP BY order_status_code
  ) AS counts
);
