execution_plan.query:
WITH dep_rows AS (
  SELECT $step1.s1__email AS email, $step1.s1__full_name AS full_name, $step1.s1__order_count AS order_count
)
SELECT email AS s2__email, full_name AS s2__full_name, order_count AS s2__order_count FROM dep_rows
UNION ALL
SELECT customers.email AS s2__email, customers.full_name AS s2__full_name, COUNT(orders.id) AS s2__order_count FROM customers JOIN orders ON customers.id = orders.customer_id GROUP BY customers.email, customers.full_name;
