SELECT AVG(EXTRACT(EPOCH FROM (o.updated_at - c.updated_at))) AS s1__avg_delay_seconds
FROM customers c
JOIN LATERAL (
  SELECT updated_at FROM orders o2 WHERE o2.customer_id = c.id ORDER BY updated_at DESC LIMIT 1
) o ON true
WHERE o.updated_at IS NOT NULL;

SELECT AVG(EXTRACT(EPOCH FROM (o.updated_at - c.updated_at))) AS s2__avg_delay_seconds
FROM customers c
JOIN LATERAL (
  SELECT updated_at FROM orders o2 WHERE o2.customer_id = c.id ORDER BY updated_at DESC LIMIT 1
) o ON true
WHERE o.updated_at IS NOT NULL;
